scan_table_check.script:
(function (engine) {

	if (x_snc_codesanity.IsCheckApplicableToRecord())
	{
		var GetStringValue = x_snc_devtools.GetStringValue;

		if (current.client_callable == true)
		{
			var strName = GetStringValue(current.name);

			var grACL = new GlideRecord('sys_security_acl');
			grACL.addQuery('sys_scope','=',current.sys_scope);
			grACL.addQuery('name','=',strName);
			grACL.addQuery('operation','=','execute');
			grACL.addQuery('type','=','client_callable_script_include');
			grACL.query();
			if (grACL.getRowCount() == 0)
			{
				engine.finding.increment();
			}
		}
	}

})(engine);
