scan_table_check.script:
(function (engine) {

	if (x_snc_codesanity.IsCheckApplicableToRecord())
	{
		var GetValue = x_snc_devtools.GetValue;

		if (current.scope != 'global')
		{
			// This is not relevant for scoped apps
			return;
		}

		var grMetadata = new GlideRecord('sys_metadata');
		grMetadata.addQuery('sys_scope','=',current.sys_id);
		grMetadata.addQuery('sys_class_name','!=','sys_scope_privilege');
		grMetadata.addQuery('sys_class_name','!=','sys_metadata_delete');
		grMetadata.query();
		while (grMetadata.next())
		{
			if (x_snc_devtools.IsOotbRecord(grMetadata) == true)
			{
				var grAppFile = x_snc_devtools.GetRecord(
					grMetadata.sys_class_name,
					grMetadata.sys_id);
				if (x_snc_devtools.IsValidRecord(grAppFile) == true)
				{
					engine.finding.setCurrentSource(grAppFile);
				}
				else
				{
					engine.finding.setCurrentSource(grMetadata);
				}
				engine.finding.increment();
			}
		}
	}

})(engine);
