sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	var strCheck = '380a75d593862210ae97b4aa6aba1090';
	
	// Test cases that provoke findings:
	
	var grApp = x_snc_devtools.GetAppRecord('x_snc_codesanity');

	var grGroup = new GlideRecord('sys_user_group');
	grGroup.initialize();
	grGroup.name = '_ATF_TEST_CODESANITY_1';
	var strGroupSysId = x_snc_devtools.GlideRecordInsert(grGroup);

	test.Log(x_snc_devtools.GetRecord('sys_user_group',strGroupSysId));

	var grRecord = new GlideRecord('user_criteria');
	grRecord.initialize();
	grRecord.name = '_ATF_TEST_CODESANITY_USER_CRITERIA_1';
	grRecord.sys_scope = grApp.sys_id;
	grRecord.group = strGroupSysId;
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	test.Log(x_snc_devtools.GetRecord('user_criteria',grRecord.sys_id));
	test.TestCase_InstanceScanCheck(strCheck,'user_criteria',strRecordSysId,true);
	
	
	// Test cases that do NOT provoke findings:
	
	var grRecord = new GlideRecord('user_criteria');
	grRecord.initialize();
	grRecord.name = '_ATF_TEST_CODESANITY_USER_CRITERIA_2';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	test.TestCase_InstanceScanCheck(strCheck,'user_criteria',strRecordSysId,false);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
