sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	var strCheck = 'bd062f523b2b661062bf612c95e45a08';

	// Test cases that do NOT provoke findings:
	
	var grRecord = new GlideRecord('sys_hub_flow');
	grRecord.initialize();
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	test.TestCase_InstanceScanCheck(strCheck,'sys_hub_flow',strRecordSysId,false);


	// Test cases that provoke findings:
	
	var testdata = new x_snc_devtools.TestDataAPI();
	var strUserSysId = testdata.CreateUser('CodeSanity - Flow or Subflow depends on data');

	var grRecordTrigger = new GlideRecord('sys_flow_record_trigger');
	grRecordTrigger.initialize();
	grRecordTrigger.condition = 'assigned_to=' + strUserSysId;
	var strRecordTriggerSysId = x_snc_devtools.GlideRecordInsert(grRecordTrigger);

	var grTriggerPlan = new GlideRecord('sys_flow_trigger_plan');
	grTriggerPlan.initialize();
	grTriggerPlan.plan_id = strRecordSysId;
	grTriggerPlan.trigger = strRecordTriggerSysId;
	var strTriggerPlanSysId = x_snc_devtools.GlideRecordInsert(grTriggerPlan);

	test.TestCase_InstanceScanCheck(strCheck,'sys_hub_flow',strRecordSysId,true);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
