sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = '6ff5e09647b65510a4b0d4fe536d4322';
	
	var grAppMenu = new GlideRecord('sys_app_application');
	grAppMenu.initialize();
	grAppMenu.sys_scope = x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id;
	grAppMenu.title = '_TEST';
	var strAppMenuSysId = x_snc_devtools.GlideRecordInsert(grAppMenu);
	
	var grAppModule = new GlideRecord('sys_app_module');
	grAppModule.initialize();
	grAppModule.sys_scope = x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id;
	grAppModule.title = '_TEST1';
	grAppModule.application = strAppMenuSysId;
	x_snc_devtools.GlideRecordInsert(grAppModule);

	test.Log('Module and menu have no roles');
	test.TestCase_InstanceScanCheck(
		strCheck,'sys_app_module',
		grAppModule.sys_id,true);

	test.Log('Module has itil role');
	grAppModule.roles = 'itil';
	x_snc_devtools.GlideRecordUpdate(grAppModule);
	test.TestCase_InstanceScanCheck(
		strCheck,'sys_app_module',
		grAppModule.sys_id,false);	
	
	test.Log('Module has snc_internal role');
	grAppModule.roles = 'snc_internal';
	x_snc_devtools.GlideRecordUpdate(grAppModule);
	test.TestCase_InstanceScanCheck(
		strCheck,'sys_app_module',
		grAppModule.sys_id,true);	

	test.Log('Menu has itil role');
	grAppMenu.roles = 'itil';
	x_snc_devtools.GlideRecordUpdate(grAppMenu);
	test.TestCase_InstanceScanCheck(
		strCheck,'sys_app_module',
		grAppModule.sys_id,false);	

	test.Log('Module has no role and Menu is "System Update Set"');
	grAppModule.roles = '';
	grAppModule.application = 'import_sets';
	x_snc_devtools.GlideRecordUpdate(grAppModule);
	test.TestCase_InstanceScanCheck(
		strCheck,'sys_app_module',
		grAppModule.sys_id,false);			

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
