sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	var strCheck = 'd93de09193566250ae97b4aa6aba1066';
	
	// Test cases that provoke findings:
	
	var grApp = x_snc_devtools.GetAppRecord('x_snc_codesanity');

	var grUser = new GlideRecord('sys_user');
	grUser.initialize();
	grUser.name = '_ATF_TEST_CODESANITY_1';
	var strUserSysId = x_snc_devtools.GlideRecordInsert(grUser);

	test.Log(x_snc_devtools.GetRecord('sys_user',strUserSysId));

	var grRecord = new GlideRecord('sc_cat_item');
	grRecord.initialize();
	grRecord.name = '_ATF_TEST_CODESANITY_SC_CAT_ITEM_1';
	grRecord.sys_scope = grApp.sys_id;
	grRecord.owner = strUserSysId;
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	test.Log(x_snc_devtools.GetRecord('sc_cat_item',grRecord.sys_id));
	test.TestCase_InstanceScanCheck(strCheck,'sc_cat_item',strRecordSysId,true);
	
	
	// Test cases that do NOT provoke findings:
	
	var grRecord = new GlideRecord('sc_cat_item');
	grRecord.initialize();
	grRecord.name = '_ATF_TEST_CODESANITY_SC_CAT_ITEM_2';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	x_snc_devtools.RecordSetValue('sc_cat_item',strRecordSysId,'owner','');
	test.Log(x_snc_devtools.GetRecord('sc_cat_item',grRecord.sys_id));
	test.TestCase_InstanceScanCheck(strCheck,'sc_cat_item',strRecordSysId,false);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
