sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	var strCheck = '85125bd23ba7661062bf612c95e45a61';
	
	// Test cases that provoke findings:
	
	var grRecord = new GlideRecord('sys_ui_policy');
	grRecord.initialize();
	grRecord.table = 'incident';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	test.TestCase_InstanceScanCheck(strCheck,'sys_ui_policy',strRecordSysId,true);
	
	
	// Test cases that do NOT provoke findings:
	
	var grRecord = new GlideRecord('sys_ui_policy');
	grRecord.initialize();
	grRecord.table = 'incident';
	grRecord.conditions = 'state=1';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	test.TestCase_InstanceScanCheck(strCheck,'sys_ui_policy',strRecordSysId,false);
	
	// Empty condition, but all UI Policy Actions relate to referenced (dot-walked) fields
	var grRecord = new GlideRecord('sys_ui_policy');
	grRecord.initialize();
	grRecord.table = 'incident';
	grRecord.conditions = '';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);
	var grUiPolicyAction = new GlideRecord('sys_ui_policy_action');
	grUiPolicyAction.initialize();
	grUiPolicyAction.ui_policy = grRecord.sys_id;
	grUiPolicyAction.field = 'caller_id.user_name';
	var strUiPolicyActionSysId = x_snc_devtools.GlideRecordInsert(grUiPolicyAction);
	test.TestCase_InstanceScanCheck(strCheck,'sys_ui_policy',strRecordSysId,false);

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
