sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = 'cd578c158349d25000d5ccc0deaad3f9';
	
	var grWS = new GlideRecord('sys_ws_definition');
	grWS.initialize();
	grWS.name = 'Test1';
	grWS.base_uri = 'test1';
	var strWSSysId = x_snc_devtools.GlideRecordInsert(grWS);

	// Default ACL is a fail
	var grWSO = new GlideRecord('sys_ws_operation');
	grWSO.initialize();
	grWSO.name = 'Resource1';
	grWSO.relative_path = 'res1';
	grWSO.web_service_definition = strWSSysId;
	grWSO.enforce_acl = 'cf9d01d3e73003009d6247e603f6a990';
	var strWSO1SysId = x_snc_devtools.GlideRecordInsert(grWSO);
	test.Log(x_snc_devtools.RenderValue(x_snc_devtools.GetRecord('sys_ws_operation',strWSO1SysId)));
	test.TestCase_InstanceScanCheck(strCheck,'sys_ws_operation',strWSO1SysId,true);	

	// Any other ACL is ok
	var grWSO = new GlideRecord('sys_ws_operation');
	grWSO.initialize();
	grWSO.name = 'Resource2';
	grWSO.relative_path = 'res2';
	grWSO.web_service_definition = strWSSysId;
	grWSO.enforce_acl = 'fb347a677713230001fb4311a81061aa';
	var strWSO2SysId = x_snc_devtools.GlideRecordInsert(grWS);
	test.Log(x_snc_devtools.RenderValue(x_snc_devtools.GetRecord('sys_ws_operation',strWSO2SysId)));
	test.TestCase_InstanceScanCheck(strCheck,'sys_ws_operation',strWSO2SysId,false);	
	
	// No ACL is ok
	var grWSO = new GlideRecord('sys_ws_operation');
	grWSO.initialize();
	grWSO.name = 'Resource3';
	grWSO.relative_path = 'res3';
	grWSO.web_service_definition = strWSSysId;
	grWSO.enforce_acl = '';
	var strWSO3SysId = x_snc_devtools.GlideRecordInsert(grWS);
	test.Log(x_snc_devtools.RenderValue(x_snc_devtools.GetRecord('sys_ws_operation',strWSO3SysId)));
	test.TestCase_InstanceScanCheck(strCheck,'sys_ws_operation',strWSO3SysId,false);	

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
