sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = 'ad58c552db93d5106a03dad1f39619d6';
	
	
	
	// Test "CodeSanity - Installation" is part of the "CodeSanity" test suite:
	test.TestCase_InstanceScanCheck(strCheck,'sys_atf_test','2c6758d3db4a8510bcced03cd3961921',false);
	
	
	var grTest = new GlideRecord('sys_atf_test');
	grTest.initialize();
	grTest.name = 'TEST1';
	grTest.sys_scope = x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id;
	var strTest1SysId = x_snc_devtools.GlideRecordInsert(grTest);
	
	// Test1 is not part of any suite:
	test.TestCase_InstanceScanCheck(strCheck,'sys_atf_test',grTest.sys_id,true);
	
	var grTestSuiteTest = new GlideRecord('sys_atf_test_suite_test');
	grTestSuiteTest.initialize();
	grTestSuiteTest.test = strTest1SysId;
	grTestSuiteTest.test_suite = '4a8550391b5b8850a89720a8ec4bcbd9'; // DevTools test suite
	grTestSuiteTest.sys_scope = x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id;
	x_snc_devtools.GlideRecordInsert(grTestSuiteTest);
	
	// Test1 is not part of the DevTools test suite - but that's not good enough:
	test.TestCase_InstanceScanCheck(strCheck,'sys_atf_test',grTest.sys_id,true);	

	
	var grTestSuiteTest = new GlideRecord('sys_atf_test_suite_test');
	grTestSuiteTest.initialize();
	grTestSuiteTest.test = strTest1SysId;
	grTestSuiteTest.test_suite = '2c6758d3db4a8510bcced03cd396191d'; // CodeSanity test suite
	grTestSuiteTest.sys_scope = x_snc_devtools.GetAppRecord('x_snc_codesanity').sys_id;
	x_snc_devtools.GlideRecordInsert(grTestSuiteTest);
	
	// Test1 is now part of the CodeSanity test suite (in the same scope) - so that should be ok:
	test.TestCase_InstanceScanCheck(strCheck,'sys_atf_test',strTest1SysId,false);		
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
