sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual) {
    var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);

    test.SetResult(true);

    var strCheck = '93c760c6c3ec56105a37f907050131d7';

	var grApp = new GlideRecord('sys_app');
	grApp.initialize();
	grApp.name = 'Test';
	grApp.scope = 'test';
	var strAppSysId = x_snc_devtools.GlideRecordInsert(grApp);

	var grRole = new GlideRecord('sys_user_role');
	grRole.initialize();
	grRole.sys_scope = strAppSysId;
	grRole.name = 'test.persona_test';
	var strRolePersonaSysId = x_snc_devtools.GlideRecordInsert(grRole);

	var grUC = new GlideRecord('user_criteria');
	grUC.initialize();
	grUC.sys_scope = strAppSysId;
	grUC.name = 'Test';
	var strUCSysId = x_snc_devtools.GlideRecordInsert(grUC);

	var strRoleItilSysId = '';

	test.TestCase_InstanceScanCheck(strCheck, 'user_criteria', strUCSysId, false);

	x_snc_devtools.RecordSetValue('user_criteria', strUCSysId,
		'role', strRoleItilSysId);
	test.TestCase_InstanceScanCheck(strCheck, 'user_criteria', strUCSysId, false);

	x_snc_devtools.RecordSetValue('user_criteria', strUCSysId,
		'role',strRolePersonaSysId);
	test.TestCase_InstanceScanCheck(strCheck, 'user_criteria', strUCSysId, true);

	x_snc_devtools.RecordSetValue('user_criteria', strUCSysId,
		'role', strRoleItilSysId+','+strRolePersonaSysId);
	test.TestCase_InstanceScanCheck(strCheck, 'user_criteria', strUCSysId, true);


    return test.GetResult();

})(outputs, steps, stepResult, assertEqual);
