sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = 'bbe450251bb77d106c17fdd49b4bcb62';
	

	// CodeSanity's own AppInstall() script complies with the rules
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include','206718d3db4a8510bcced03cd3961997',false);

	// DevTools' GenerateAndSendCredentialsToUser() function may use .update()
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include','9650c8191b6a2110cd6153d16b4bcb82',false);

	var grApp = new GlideRecord('sys_app');
	grApp.initialize();
	grApp.name = '_TEST_App';
	grApp.version = '1.0.0';
	grApp.scope = 'x_snc_testapp';
	var strAppSysId = x_snc_devtools.GlideRecordInsert(grApp);

	var grRecord = new GlideRecord('sys_script_include');
	grRecord.initialize();
	grRecord.name = 'AppInstall';
	grRecord.sys_scope = strAppSysId;
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grRecord);

	var strScript =
		'function AppInstall()\n'
		+'{ return true }';
	x_snc_devtools.RecordSetValue('sys_script_include',strRecordSysId,'script',strScript);
	test.Log(strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);

	strScript =
		'function AppInstall()\n'
		+'{'
		+'   var gr = new GlideRecord("sys_script_include");'
		+'   return true;'
		+'}';
	x_snc_devtools.RecordSetValue('sys_script_include',strRecordSysId,'script',strScript);
	test.Log(strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);

	strScript =
		'function AppInstall()\n'
		+'{'
		+'   var gr = new GlideRecord("sys_script_include");'
		+'   gr.initialize();'	
		+'   gr.insert();'		
		+'   return true;'
		+'}';
	x_snc_devtools.RecordSetValue('sys_script_include',strRecordSysId,'script',strScript);
	test.Log(strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);


	strScript =
		'function AppInstall()\n'
		+'{'
		+'   var gr = new GlideRecord("sys_script_include");'
		+'   gr.get("xyz");'
		+'   gr.update();'
		+'   return true;'
		+'}';
	x_snc_devtools.RecordSetValue('sys_script_include',strRecordSysId,'script',strScript);
	test.Log(strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);

	strScript =
		'function AppInstall()\n'
		+'{'
		+'   var gr = new GlideRecord("sys_script_include");'
		+'   gr.get("xyz");'
		+'   // we do not use gr.update()'
		+'   return true;'
		+'}';
	x_snc_devtools.RecordSetValue('sys_script_include',strRecordSysId,'script',strScript);
	test.Log(strScript);
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);

	

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
