sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = '8bce3e3783f45e1000d5ccc0deaad3d4';
	
	// No ACL is a fail
	var grWS = new GlideRecord('sys_ws_definition');
	grWS.initialize();
	grWS.name = 'Test1';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grWS);
	test.Log(x_snc_devtools.RenderValue(x_snc_devtools.GetRecord('sys_ws_definition',strRecordSysId)));
	test.TestCase_InstanceScanCheck(strCheck,'sys_ws_definition',strRecordSysId,true);

	// Default ACL is a fail
	var grWS = new GlideRecord('sys_ws_definition');
	grWS.initialize();
	grWS.name = 'Test2';
	grWS.enforce_acl = 'cf9d01d3e73003009d6247e603f6a990';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grWS);
	test.Log(x_snc_devtools.RenderValue(x_snc_devtools.GetRecord('sys_ws_definition',strRecordSysId)));
	test.TestCase_InstanceScanCheck(strCheck,'sys_ws_definition',strRecordSysId,true);	

	// Any other ACL is ok
	var grWS = new GlideRecord('sys_ws_definition');
	grWS.initialize();
	grWS.name = 'Test3';
	grWS.enforce_acl = 'fb347a677713230001fb4311a81061aa';
	var strRecordSysId = x_snc_devtools.GlideRecordInsert(grWS);
	test.Log(x_snc_devtools.RenderValue(x_snc_devtools.GetRecord('sys_ws_definition',strRecordSysId)));
	test.TestCase_InstanceScanCheck(strCheck,'sys_ws_definition',strRecordSysId,false);	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
